onMounted(async () => {
    const rr = await docDetail({ docId: rowkey })
    console.log(rr)
    if (rr.code === 0 && rr.success) {
        // qwContent 是原始 html，需要把头和尾摘掉，这样 rich-text 才能识别
        let result = rr.data.result
        result.qwContent = result.qwContent.replace(/.*?<body.*?>/sig, "")
        // console.log(result.qwContent)
        result.qwContent = result.qwContent.replace(/<\/body>.*/sig, "")
        // console.log(result.qwContent)
        doc.value = result
        console.log(doc.value)
    } else {
        showToast(`加载失败(${rr.code})`)
    }
})

</script>

